import React, { useState, useEffect, useRef } from "react";
import { db, auth } from "../firebase";
import SendMessage from "./SendMessage";
import SignOut from "./SignOut";

function Chat() {
  const [mesaages, setMessaegs] = useState([]);

  useEffect(() => {
    db.collection("messages")
      .orderBy("createdAt")
      .limit(50)
      .onSnapshot((snapshot) => {
        setMessaegs(snapshot.docs.map((doc) => doc.data()));
      });
  }, [mesaages]);
  return (
    <div>
      <SignOut />
      {mesaages.map(({ id, text, photoURL }) => {
        <div key={id}>
          <img src={photoURL} alt="" />
          <p>{text}</p>
        </div>;
      })}
    </div>
  );
}

export default Chat;
